package curso2;

public class ExpressaoCondicionalTernaria {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Boolean saida;
		int valor = 2;
		// para usar operador ternario é preciso jogar o resultado
		// numa variável, sendo que antes dos dois pontos é o verdadeiro
		// e depois dos dois pontos é o resultado para falso
		saida  = (2 > 4) ? true : false;
		System.out.println("Saida da expressão 2 > 4: " + saida);
	}

}
